mapper.select:
select qd.dimension_id, s.scale_name, qd.dimension_name, qd.scale_id, s.scale_type, qd.dimension_code, qd.create_date, qd.update_date from psyc_question_dimension qd left join psyc_scale s on qd.scale_id = s.scale_id where 1 = 1
and s.scale_id =#{scaleId}
and s.scale_type=#{scaleType}
and qd.dimension_name like CONCAT(#{dimensionName},'%')

select qd.dimension_id, s.scale_name, qd.dimension_name, qd.scale_id, s.scale_type, qd.dimension_code, qd.create_date, qd.update_date from psyc_question_dimension qd left join psyc_scale s on qd.scale_id = s.scale_id where 1 = 1
and s.scale_id =#{scaleId,jdbcType=INTEGER}
and s.scale_type=#{scaleType,jdbcType=INTEGER}
